import React, { useState } from 'react';
import ExcelReader from 'react-excel-renderer';

const ExcelUploader = () => {

    const [data, setData] = useState([]);

    const handleFileUploader = (event) => {
        const file = event.target.files[0];

        ExcelReader(file, (err, result) => {
            if(err){
                console.log(err);
            } else{
                setData(result);
            }
        });
    };



  return (
    <div>
        <h1 className="text-3xl font-bold p-8">


      <label className="block mb-2 text-sm font-medium text-gray-900 dark:text-black" for="file_input">Upload file</label>
      <input className="block w-96 text-sm text-gray-900 border border-gray-300 rounded-lg 
        cursor-pointer bg-gray-50 dark:text-gray-400 focus:outline-none dark:bg-gray-700 
        dark:border-gray-600 dark:placeholder-gray-400" id="file_input" type="file"  
        onChange={handleFileUploader}
      />
      <button type="button" className="focus:outline-none text-white bg-purple-700 
      hover:bg-purple-800 focus:ring-4 focus:ring-purple-300 font-medium rounded-lg
      text-sm px-5 py-1.5 mb-2 dark:bg-purple-600 dark:hover:bg-purple-700 dark:focus:ring-purple-900"
      onClick={()=>console.log(data)}
      >Submit</button>

    </h1>

    </div>
  )
}
export default ExcelUploader;
